import React from "react";

function SummaryDisplay({ data }) {
  return (
    <div className="summary-box">
      <h2>üìù Transcript</h2>
      <p>{data.transcript || "No transcript available"}</p>

      <h2>üí° Summary</h2>
      <p>{data.summary || "No summary generated"}</p>

      <h2>‚úÖ Action Items</h2>
      <ul>
        {data.action_items && data.action_items.length > 0 ? (
          data.action_items.map((item, index) => <li key={index}>{item}</li>)
        ) : (
          <li>No action items found</li>
        )}
      </ul>
    </div>
  );
}

export default SummaryDisplay;
