import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import SummaryDisplay from "./components/SummaryDisplay";
import "./App.css";

function App() {
  const [summaryData, setSummaryData] = useState(null);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Meeting Intelligence</h1>
        <p>Transform audio meetings into actionable insights</p>
      </header>

      <main>
        {/* Upload Card */}
        <FileUpload setSummaryData={setSummaryData} />
        
        {/* Results Container */}
        {summaryData && (
          <div className="results-container">
            {/* Processing Success Card */}
            <div className="processing-message">
              <p>✅ Analysis complete! Your meeting intelligence is ready.</p>
            </div>

            {/* File Info Card */}
            <div className="file-info-card">
              <div className="file-info-grid">
                <div className="file-info-item">
                  <div className="file-info-label">File Name</div>
                  <div className="file-info-value">{summaryData.fileInfo?.name}</div>
                </div>
                <div className="file-info-item">
                  <div className="file-info-label">File Size</div>
                  <div className="file-info-value">{summaryData.fileInfo?.size}</div>
                </div>
                <div className="file-info-item">
                  <div className="file-info-label">File Type</div>
                  <div className="file-info-value">{summaryData.fileInfo?.type}</div>
                </div>
                <div className="file-info-item">
                  <div className="file-info-label">Processed</div>
                  <div className="file-info-value">{summaryData.fileInfo?.processed ? 'Yes' : 'No'}</div>
                </div>
              </div>
            </div>

            {/* Summary Display */}
            <SummaryDisplay data={summaryData} />
          </div>
        )}
      </main>

      <footer className="app-footer">
        <p>© 2024 Meeting Intelligence Platform | Enterprise-Grade Audio Analysis</p>
      </footer>
    </div>
  );
}

export default App;
