import React, { useState } from "react";
import FileUpload from "./components/FileUpload";
import SummaryDisplay from "./components/SummaryDisplay";
import "./App.css";

function App() {
  const [summaryData, setSummaryData] = useState(null);
  const [loading, setLoading] = useState(false);

  return (
    <div className="container">
      <h1>üéôÔ∏è Meeting Summarizer</h1>
      <p>Upload your meeting audio to get a transcript and summary</p>

      <FileUpload setSummaryData={setSummaryData} setLoading={setLoading} />
      {loading && <div className="loader">Processing...</div>}

      {summaryData && <SummaryDisplay data={summaryData} />}
    </div>
  );
}

export default App;
